{
        "id": 1,
        "name": "Alucard",
        "role": ["Fighter", "Assassin"],
        "special": ["Charge", "Crowd Control"],
        "avatar": "asdasd",
        "abilities": [
            {
                "name": "Pursuit",
                "description": "Alucard's skills deal 1.3 damage when hitting only one enemy hero or Creep. After each skill cast, his next Basic Attack deals 1.4 times damage and allows him to dash to the target's location."
            },
            {
                "name": "Groundsplitter",
                "description": "Alucard rolls to the target location and slams his blade on the ground, dealing 270–370 (+110% Extra Physical Attack) Physical Damage to enemies hit and slowing them by 40% for 2s."
            },
            {
                "name": "Whirling Smash",
                "description": "Alucard launches a whirling slash, dealing 320–420 (+120% Extra Physical Attack) Physical Damage to nearby enemies."
            },
            {
                "name": "Fission Wave",
                "description": "Passive: Alucard gains 5-15% extra Hybrid Lifesteal. Alucard absorbs the energy of enemies within the designated area, gaining 15% / 20% / 25% Hybrid Lifesteal and reducing the cooldown of other skills to 50%, slowing enemies by 40% for 4 seconds, and marking them for 6 seconds, during which his attacks against marked enemies will always trigger Pursuit Pursuit. Use again: Alucard releases a shockwave in the target direction, dealing 400–700 (+200% Extra Physical Attack) Physical Damage to enemies hit." 
            }
        ],
        "price": {
            "battelpoint": 32000,
            "diamond": null,
            "ticket": 399
        },
        "_storyId": 6
    }

